mapper.select:
select #{sido} as address, longitude, latitude
from addressinfo
where address_level = 1 and address like #{sido}

select longitude, latitude
from houseinfo
where apt_id = #{aptId}

select store_id as storeId, name, jibun, longitude, latitude
from storeinfo
WHERE longitude between #{startLongitude} and #{endLongitude}
AND latitude BETWEEN #{startLatitude} AND #{endLatitude}
AND category = #{level}

select store_id as storeId, name, jibun, tel, longitude, latitude
from storeinfo
WHERE longitude between #{startLongitude} and #{endLongitude}
  AND latitude BETWEEN #{startLatitude} AND #{endLatitude}
  AND category = #{level}
